"use client";

import { Divider, Flex, Input, Radio, Row, Spin, Typography } from "antd";
import { RadioChangeEvent } from "antd/lib";
import React, { useEffect, useState } from "react";
import { CollectionCard } from '../../components/collectionCard';
import { SampleCard } from '../../components/sampleCard';
import { CollectionDTO, SampleDTO, SamplePreviewDTO } from '../../lib/Types';

const { Search } = Input;
const { Title } = Typography;	

enum SearchType {
	Sample,
	Collection,
}

const columnsSizes = {
	xs: 24 / 1,
	sm: 24 / 2,
	md: 24 / 3,
	lg: 24 / 4,
};
const imageFallback: string =
	"data:image/png;base64,";

const cardStyle: React.CSSProperties = {
	display: "flex",
	flexDirection: "column",
	height: "100%",
};

type sampleData = (Partial<SamplePreviewDTO> & {image?: string});

const initialContent: sampleData[] = [];
for(var i = 0; i < 10; ++i) initialContent.push({ name: `Element ${i}`, description: `description text`, image: `` });

const SearchPage: React.FC = () => {
	const [searchType, setSearchType] = useState<SearchType>(SearchType.Sample);
	const [elements, setElements] = useState<sampleData[]>(initialContent);
	const [searchQuery, setSearchQuery] = useState<string>();
	const [loading, setLoading] = useState<boolean>(false);

	useEffect(() => {
		if (!searchQuery || searchQuery.length === 0) return;

		const originalQuery = searchQuery;

		fetch(
			"http://localhost:5047/api/samples/previews?" +
				new URLSearchParams({
					name: searchQuery,
				})
		)
			.then((response: Response) => response.json())
			.then((json) => {
				if (searchQuery != originalQuery) return;

				loadData(json);
			});
	}, [searchQuery]);

	const loadData = (data: SamplePreviewDTO[]) => {
		/// TODO SHUT THE FUCK UP MAKE THIS WORK WITH THE CORRECT FORMAT
		setElements(data as any);
		setLoading(false);
	};

	const onSearchTypeChange = ({ target: { value } }: RadioChangeEvent) => setSearchType(value);

	const onSearch = (value: string) => {
		if (!value || value === "" || value === searchQuery) return;

		setLoading(true);
		setSearchQuery(value);
	};

	const searchTypes: string[] = [SearchType[SearchType.Sample], SearchType[SearchType.Collection]];

	return (
		<>
			<Title>Search</Title>
			<Flex vertical={true} gap="small">
				<Search placeholder="e.g. Andesite" onSearch={onSearch} />
				<Radio.Group options={searchTypes} optionType="button" defaultValue={SearchType[searchType]} onChange={onSearchTypeChange} />
			</Flex>
			<Divider />
			{loading 
				?	<Spin size="large" tip="Loading..." />
				: <Row gutter={[15, 15]}>
						{
							elements?.map((e: any, i: number) => {
								return <React.Fragment key={i}>
									{
									/// Check Card type here, easier
									searchType === SearchType.Sample 
										? <SampleCard {...e as SampleDTO} columnsSizes={columnsSizes} cardStyle={cardStyle} imageFallback={imageFallback} />
										: <CollectionCard {...e as CollectionDTO} columnsSizes={columnsSizes} cardStyle={cardStyle} /> 
									}
								</React.Fragment>
							}
						)
					}
				</Row>
			}
		</>
	);
}; 

export default SearchPage;
